INSERT INTO snacks (id, name, content, instruction, source) VALUES (1, 'Smoked Salmon Crackers', 'Cream Cheese, Crackers, Smoked Salmon, Cucumber, Salt', 'Thinly spread the cream cheese on each cracker, being careful to not break the crackers!  Top each cracker spread with a cucumber slice and a piece of smoked salmon. Sprinkle with flaky sea salt, if desired. Place them on a serving platter and serve immediately or chill until ready to eat.', 'http://savoryexperiments.com/smoked-salmon-with-creamy-pesto-crackers/');
