INSERT INTO snacks (id, name, content, instruction, source) VALUES (7, 'Tortilla Chips with Guacamole ', 'Avocados, Lime Juice, Garlic, Onion, Tomato, Salt, Pepper, Tortilla Chips  ', 'Halve avocados lengthways, remove seed, and scoop flesh into a medium bowl.  Add lime juice and mash to combine. Add garlic, onion, tomato, salt and pepper,  then mix well. Serve with Tortilla Chips.', 'https://www.taste.com.au/recipes/guacamole-crispy-tortilla-chips/24d0551d-392e-44b2-a12c-e26d95f09a3a');
INSERT INTO snacks (id, name, content, instruction, source) VALUES (8, 'Fruit Skewers', 'Purple Grapes, Strawberries, Orange, Pineapple, Kiwi, Wooden Skewers', 'Carefully arrange each fruit onto your wooden skewers in rainbow order. Then enjoy!', 'https://www.ambitiouskitchen.com/fruit-skewers/');
