INSERT INTO snacks (id, name, content, instruction, source) VALUES (14, 'Antipasti skewers', 'Feta Cheese, Serrano Ham, Pickled Cherry Peppers, Olives, Cocktail Sticks', 'Cut the feta into small cubes. Tear the Serrano ham into long pieces.  Thread a cube of feta, a piece of ham, a drained pickled cherry pepper and an olive into long cocktail sticks.', 'https://www.bbcgoodfood.com/recipes/antipasti-skewers');
