INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11000, 'Mojito', 'Cocktail', 'Highball glass', 'Muddle mint leaves with sugar and lime juice. Add a splash of soda water and fill the glass with cracked ice. Pour the rum and top with soda water. Garnish and serve with straw.', 'https://www.thecocktaildb.com/images/media/drink/metwgh1606770327.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11001, 'Old Fashioned', 'Cocktail', 'Old-fashioned glass', 'Place sugar cube in old fashioned glass and saturate with bitters, add a dash of plain water. Muddle until dissolved. Fill the glass with ice cubes and add whiskey.  Garnish with orange twist, and a cocktail cherry.', 'https://www.thecocktaildb.com/images/media/drink/vrwquq1478252802.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11002, 'Long Island Tea', 'Ordinary Drink', 'Highball glass', 'Combine all ingredients (except cola) and pour over ice in a highball glass. Add the splash of cola for color. Decorate with a slice of lemon and serve.', 'https://www.thecocktaildb.com/images/media/drink/nkwr4c1606770558.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11003, 'Negroni', 'Ordinary Drink', 'Old-fashioned glass', 'Stir into glass over ice, garnish and serve.', 'https://www.thecocktaildb.com/images/media/drink/qgdu971561574065.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11005, 'Dry Martini', 'Cocktail', 'Cocktail glass', 'Straight: Pour all ingredients into mixing glass with ice cubes. Stir well. Strain in chilled martini cocktail glass. Squeeze oil from lemon peel onto the drink, or garnish with olive.', 'https://www.thecocktaildb.com/images/media/drink/6ck9yi1589574317.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11007, 'Margarita', 'Ordinary Drink', 'Cocktail glass', 'Rub the rim of the glass with the lime slice to make the salt stick to it. Take care to moisten only the outer rim and sprinkle the salt on it. The salt should present to the lips of the imbiber and never mix into the cocktail. Shake the other ingredients with ice, then carefully pour into the glass.', 'https://www.thecocktaildb.com/images/media/drink/5noda61589575158.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11008, 'Manhattan', 'Cocktail', 'Cocktail glass', 'Stirred over ice, strained into a chilled glass, garnished, and served up.', 'https://www.thecocktaildb.com/images/media/drink/yk70e31606771240.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11009, 'Moscow Mule', 'Punch / Party Drink', 'Copper Mug', 'Combine vodka and ginger beer in a highball glass filled with ice. Add lime juice. Stir gently. Garnish.', 'https://www.thecocktaildb.com/images/media/drink/3pylqc1504370988.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11010, 'After Dinner Cocktail', 'Ordinary Drink', 'Cocktail glass', 'Shake all ingredients (except lime wedge) with ice and strain into a cocktail glass. Add the wedge of lime and serve.', 'https://www.thecocktaildb.com/images/media/drink/vtytxq1483387578.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11011, 'After Supper Cocktail', 'Ordinary Drink', 'Cocktail glass', 'Shake all ingredients with ice, strain into a cocktail glass, and serve.', 'https://www.thecocktaildb.com/images/media/drink/quyxwu1483387610.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11012, 'Alabama Slammer', 'Ordinary Drink', 'Highball glass', 'Pour all ingredients (except for lemon juice) over ice in a highball glass. Stir, add a dash of lemon juice, and serve.', 'https://www.thecocktaildb.com/images/media/drink/jntghf1606771886.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11013, 'Alaska Cocktail', 'Ordinary Drink', 'Cocktail glass', 'Stir all ingredients with ice, strain contents into a cocktail glass. Drop in a twist of lemon and serve.', 'https://www.thecocktaildb.com/images/media/drink/wsyryt1483387720.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11014, 'Alexander', 'Ordinary Drink', 'Cocktail glass', 'Shake all ingredients with ice and strain contents into a cocktail glass. Sprinkle nutmeg on top and serve.', 'https://www.thecocktaildb.com/images/media/drink/0clus51606772388.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11016, 'Brandy Alexander', 'Ordinary Drink', 'Whiskey Glass', 'Shake all ingredients (except nutmeg) with ice and strain contents into a cocktail glass. Sprinkle nutmeg on top and serve.', 'https://www.thecocktaildb.com/images/media/drink/mlyk1i1606772340.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11019, 'Alfie Cocktail', 'Ordinary Drink', 'Cocktail glass', 'Combine and shake all ingredients with ice, strain into a cocktail glass, and serve.', 'https://www.thecocktaildb.com/images/media/drink/ypxsqy1483387829.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11020, 'Algonquin', 'Ordinary Drink', 'Cocktail glass', 'Combine and shake all ingredients with ice, strain contents into a cocktail glass, and serve.', 'https://www.thecocktaildb.com/images/media/drink/uwryxx1483387873.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11021, 'Allegheny', 'Ordinary Drink', 'Cocktail glass', 'Shake all ingredients (except lemon peel) with ice and strain into a cocktail glass. Top with the twist of lemon peel and serve.', 'https://www.thecocktaildb.com/images/media/drink/uwvyts1483387934.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11022, 'Allies Cocktail', 'Ordinary Drink', 'Cocktail glass', 'Stir all ingredients with ice, strain contents into a cocktail glass, and serve.', 'https://www.thecocktaildb.com/images/media/drink/qvprvp1483388104.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11023, 'Almeria', 'Ordinary Drink', 'Cocktail glass', 'In a shaker half-filled with ice cubes, combine all of the ingredients. Shake well. Strain into a cocktail glass.', 'https://www.thecocktaildb.com/images/media/drink/rwsyyu1483388181.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11024, 'Almond Joy', 'Ordinary Drink', 'Cocktail glass', 'Shake all ingredients with ice, strain into a cocktail glass, and serve.', 'https://www.thecocktaildb.com/images/media/drink/xutuqs1483388296.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11025, 'Amaretto And Cream', 'Ordinary Drink', 'Cocktail glass', 'Shake well with cracked ice, strain contents into a cocktail glass, and serve.', 'https://www.thecocktaildb.com/images/media/drink/dj8n0r1504375018.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11026, 'Amaretto Mist', 'Ordinary Drink', 'Old-fashioned glass', 'Pour amaretto in an old-fashioned glass over crushed ice. Add the wedge of lime and serve. (A wedge of lemon may be substituted for lime, if preferred.)', 'https://www.thecocktaildb.com/images/media/drink/utpxxq1483388370.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11027, 'Amaretto Rose', 'Ordinary Drink', 'Collins glass', 'Pour amaretto and lime juice over ice in a collins glass. Fill with club soda and serve.', 'https://www.thecocktaildb.com/images/media/drink/3jm41q1493069578.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11028, 'Amaretto Stinger', 'Ordinary Drink', 'Cocktail glass', 'Shake ingredients well with cracked ice, strain into a cocktail glass, and serve.', 'https://www.thecocktaildb.com/images/media/drink/vvop4w1493069934.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11029, 'Amaretto Tea', 'Ordinary Drink', 'Pousse cafe glass', 'Pour hot tea into a pousse-cafe glass, using a spoon in glass to prevent cracking. Add amaretto, but do not stir. Top with chilled whipped cream and serve.', 'https://www.thecocktaildb.com/images/media/drink/b7qzo21493070167.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11034, 'Angel Face', 'Ordinary Drink', 'Cocktail glass', 'Shake all ingredients with ice and strain contents into a cocktail glass.', 'https://www.thecocktaildb.com/images/media/drink/vaukir1606772580.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11046, 'Applecar', 'Ordinary Drink', 'Cocktail glass', 'Shake all ingredients with ice, strain into a cocktail glass, and serve.', 'https://www.thecocktaildb.com/images/media/drink/sbffau1504389764.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11050, 'Apricot Lady', 'Ordinary Drink', 'Old-fashioned glass', 'In a shaker half-filled with ice cubes, combine the rum, apricot brandy, triple sec, lemon juice, and egg white. Shake well. Strain into an old-fashioned glass almost filled with ice cubes. Garnish with the orange slice.', 'https://www.thecocktaildb.com/images/media/drink/7ityp11582579598.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11052, 'Archbishop', 'Ordinary Drink', 'Old-fashioned glass', 'In an old-fashioned glass almost filled with ice cubes, combine all of the ingredients. Stir well.', 'https://www.thecocktaildb.com/images/media/drink/4g6xds1582579703.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11053, 'Arise My Love', 'Ordinary Drink', 'Champagne flute', 'Put creme de menthe into a champagne flute. Fill with chilled champagne and serve.', 'https://www.thecocktaildb.com/images/media/drink/wyrrwv1441207432.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11054, 'Arthur Tompkins', 'Ordinary Drink', 'Whiskey sour glass', 'In a shaker half-filled with ice cubes, combine the gin, Grand Marnier, and lemon juice. Shake well. Strain into a sour glass and garnish with the lemon twist.', 'https://www.thecocktaildb.com/images/media/drink/7onfhz1493067921.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11055, 'Artillery', 'Ordinary Drink', 'Cocktail glass', 'Stir all ingredients with ice, strain into a cocktail glass, and serve.', 'https://www.thecocktaildb.com/images/media/drink/g1vnbe1493067747.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11060, 'Balmoral', 'Ordinary Drink', 'Cocktail glass', 'In a mixing glass half-filled with ice cubes, combine all of the ingredients. Stir well. Strain into a cocktail glass.', 'https://www.thecocktaildb.com/images/media/drink/vysuyq1441206297.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11064, 'Banana Daiquiri', 'Ordinary Drink', 'Champagne flute', 'Pour all ingredients into shaker with ice cubes. Shake well. Strain in chilled cocktail glass.', 'https://www.thecocktaildb.com/images/media/drink/k1xatq1504389300.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11084, 'Bermuda Highball', 'Ordinary Drink', 'Highball glass', 'Pour brandy, gin, and dry vermouth into a highball glass over ice cubes. Fill with carbonated water and stir. Add the twist of lemon and serve. (Ginger ale may be substituted for carbonated water, if preferred.)', 'https://www.thecocktaildb.com/images/media/drink/qrvtww1441206528.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11102, 'Black Russian', 'Ordinary Drink', 'Old-fashioned glass', 'Pour the ingredients into an old fashioned glass filled with ice cubes. Stir gently.', 'https://www.thecocktaildb.com/images/media/drink/8oxlqf1606772765.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11106, 'Blackthorn', 'Ordinary Drink', 'Cocktail glass', 'Stir sloe gin and vermouth with ice and strain into a cocktail glass. Add the twist of lemon peel and serve.', 'https://www.thecocktaildb.com/images/media/drink/dgj92f1616098672.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11112, 'Bloody Maria', 'Ordinary Drink', 'Old-fashioned glass', 'Shake all ingredients (except lemon slice) with cracked ice and strain into an old-fashioned glass over ice cubes. Add the slice of lemon and serve.', 'https://www.thecocktaildb.com/images/media/drink/yz0j6z1504389461.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11113, 'Bloody Mary', 'Ordinary Drink', 'Old-fashioned glass', 'Stirring gently, pour all ingredients into highball glass. Garnish.', 'https://www.thecocktaildb.com/images/media/drink/t6caa21582485702.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11117, 'Blue Lagoon', 'Ordinary Drink', 'Highball glass', 'Pour vodka and curacao over ice in a highball glass. Fill with lemonade, top with the cherry, and serve.', 'https://www.thecocktaildb.com/images/media/drink/5wm4zo1582579154.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11118, 'Blue Margarita', 'Ordinary Drink', 'Cocktail glass', 'Rub rim of cocktail glass with lime juice. Dip rim in coarse salt. Shake tequila, blue curacao, and lime juice with ice, strain into the salt-rimmed glass, and serve.', 'https://www.thecocktaildb.com/images/media/drink/bry4qh1582751040.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11119, 'Blue Mountain', 'Ordinary Drink', 'Old-fashioned glass', 'In a shaker half-filled with ice cubes, combine all of the ingredients. Shake well. Strain into an old-fashioned glass almost filled with ice cubes.', 'https://www.thecocktaildb.com/images/media/drink/bih7ln1582485506.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11120, 'Bluebird', 'Ordinary Drink', 'Cocktail glass', 'In a mixing glass half-filled with crushed ice, combine the gin, triple sec, Curacao, and bitters. Stir well. Strain into a cocktail glass and garnish with the lemon twist and the cherry.', 'https://www.thecocktaildb.com/images/media/drink/5jhyd01582579843.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11121, 'Bobby Burns Cocktail', 'Ordinary Drink', 'Cocktail glass', 'Stir all ingredients (except lemon peel) with ice and strain into a cocktail glass. Add the twist of lemon peel and serve.', 'https://www.thecocktaildb.com/images/media/drink/km6se51484411608.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11124, 'Boomerang', 'Ordinary Drink', 'Cocktail glass', 'In a mixing glass half-filled with ice cubes, combine the gin, vermouth, bitters, and maraschino liqueur. Stir well. Strain into a cocktail glass and garnish with the cherry.', 'https://www.thecocktaildb.com/images/media/drink/3m6yz81504389551.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11128, 'Boston Sidecar', 'Ordinary Drink', 'Cocktail glass', 'Shake all ingredients with ice, strain into a cocktail glass, and serve.', 'https://www.thecocktaildb.com/images/media/drink/qzs5d11504365962.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11129, 'Boston Sour', 'Ordinary Drink', 'Whiskey sour glass', 'Shake juice of lemon, powdered sugar, blended whiskey, and egg white with cracked ice and strain into a whiskey sour glass. Add the slice of lemon, top with the cherry, and serve.', 'https://www.thecocktaildb.com/images/media/drink/kxlgbi1504366100.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11145, 'Classic Old-Fashioned', 'Ordinary Drink', 'Old-fashioned glass', 'In an old-fashioned glass, muddle the bitters and water into the sugar cube, using the back of a teaspoon. Almost fill the glass with ice cubes and add the bourbon. Garnish with the orange slice and the cherry. Serve with a swizzle stick.', 'https://www.thecocktaildb.com/images/media/drink/w8cxqv1582485254.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11146, 'Bourbon Sling', 'Ordinary Drink', 'Highball glass', 'In a shaker half-filled with ice cubes, combine the sugar, water, lemon juice, and bourbon. Shake well. Strain well. Strain into a highball glass. Garnish with the lemon twist.', 'https://www.thecocktaildb.com/images/media/drink/3s36ql1504366260.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11147, 'Bourbon Sour', 'Ordinary Drink', 'Whiskey sour glass', 'In a shaker half-filled with ice cubes, combine the bourbon, lemon juice, and sugar. Shake well. Strain into a whiskey sour glass, garnish with the orange slice and cherry.', 'https://www.thecocktaildb.com/images/media/drink/dms3io1504366318.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11149, 'Boxcar', 'Ordinary Drink', 'Whiskey sour glass', 'In a shaker half-filled with ice cubes, combine all of the ingredients. Shake well. Strain into a sour glass.', 'https://www.thecocktaildb.com/images/media/drink/pwgtpa1504366376.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11157, 'Brandy Cobbler', 'Ordinary Drink', 'Old-fashioned glass', 'In an old-fashioned glass, dissolve the sugar in the club soda. Add crushed ice until the glass is almost full. Add the brandy. Stir well. Garnish with the cherry and the orange and lemon slices.', 'https://www.thecocktaildb.com/images/media/drink/5xgu591582580586.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11164, 'Brandy Flip', 'Ordinary Drink', 'Whiskey sour glass', 'In a shaker half-filled with ice cubes, combine the brandy, egg, sugar, and cream. Shake well. Strain into a sour glass and garnish with the nutmeg.', 'https://www.thecocktaildb.com/images/media/drink/6ty09d1504366461.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11170, 'Brandy Sour', 'Ordinary Drink', 'Whiskey sour glass', 'Shake brandy, juice of lemon, and powdered sugar with ice and strain into a whiskey sour glass. Decorate with the lemon slice, top with the cherry, and serve.', 'https://www.thecocktaildb.com/images/media/drink/b1bxgq1582484872.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11202, 'Caipirinha', 'Ordinary Drink', 'Old-fashioned glass', 'Place lime and sugar into old fashioned glass and muddle (mash the two ingredients together using a muddler or a wooden spoon). Fill the glass with ice and add the Cacha√ßa.', 'https://www.thecocktaildb.com/images/media/drink/jgvn7p1582484435.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11205, 'California Lemonade', 'Ordinary Drink', 'Collins glass', 'Shake all ingredients (except carbonated water) with ice and strain into a collins glass over shaved ice. Fill with carbonated water and stir. Decorate with slices of orange and lemon. Add the cherry and serve with a straw.', 'https://www.thecocktaildb.com/images/media/drink/q5z4841582484168.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11222, 'Casa Blanca', 'Ordinary Drink', 'Cocktail glass', 'Shake all ingredients with ice, strain into a cocktail glass, and serve.', 'https://www.thecocktaildb.com/images/media/drink/usspxq1441553762.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11224, 'Casino Royale', 'Ordinary Drink', 'Whiskey sour glass', 'In a shaker half-filled with ice cubes, combine all of the ingredients. Shake well. Strain into a sour glass.', 'https://www.thecocktaildb.com/images/media/drink/3qpv121504366699.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11227, 'Champagne Cocktail', 'Ordinary Drink', 'Champagne flute', 'Add dash of Angostura bitter onto sugar cube and drop it into champagne flute. Add cognac followed by gently pouring chilled champagne. Garnish with orange slice and maraschino cherry.', 'https://www.thecocktaildb.com/images/media/drink/t5pv461606773026.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11239, 'Cherry Rum', 'Ordinary Drink', 'Cocktail glass', 'Shake all ingredients with ice, strain into a cocktail glass, and serve.', 'https://www.thecocktaildb.com/images/media/drink/twsuvr1441554424.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11242, 'Chicago Fizz', 'Ordinary Drink', 'Highball glass', 'Shake all ingredients (except carbonated water) with ice and strain into a highball glass over two ice cubes. Fill with carbonated water, stir, and serve.', 'https://www.thecocktaildb.com/images/media/drink/qwvwqr1441207763.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11243, 'Chocolate Black Russian', 'Ordinary Drink', 'Champagne flute', 'Combine all ingredients in an electric blender and blend at a low speed for a short length of time. Pour into a chilled champagne flute and serve.', 'https://www.thecocktaildb.com/images/media/drink/yyvywx1472720879.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11251, 'City Slicker', 'Ordinary Drink', 'Cocktail glass', 'In a shaker half-filled with ice cubes, combine all of the ingredients. Shake well. Strain into a cocktail glass.', 'https://www.thecocktaildb.com/images/media/drink/dazdlg1504366949.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11255, 'Clove Cocktail', 'Ordinary Drink', 'Cocktail glass', 'Stir all ingredients with ice, strain into a cocktail glass, and serve.', 'https://www.thecocktaildb.com/images/media/drink/qxvtst1461867579.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11288, 'Cuba Libre', 'Ordinary Drink', 'Highball glass', 'Build all ingredients in a Collins glass filled with ice. Garnish with lime wedge.', 'https://www.thecocktaildb.com/images/media/drink/wmkbfj1606853905.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11320, 'Dragonfly', 'Ordinary Drink', 'Highball glass', 'In a highball glass almost filled with ice cubes, combine the gin and ginger ale. Stir well. Garnish with the lime wedge.', 'https://www.thecocktaildb.com/images/media/drink/uc63bh1582483589.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11326, 'Dubonnet Cocktail', 'Ordinary Drink', 'Cocktail glass', 'Stir all ingredients (except lemon peel) with ice and strain into a cocktail glass. Add the twist of lemon peel and serve.', 'https://www.thecocktaildb.com/images/media/drink/pfz3hz1582483111.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11338, 'English Highball', 'Ordinary Drink', 'Highball glass', 'Pour brandy, gin, and sweet vermouth into a highball glass over ice cubes. Fill with carbonated water. Add the twist of lemon peel, stir, and serve. (Ginger ale may be substituted for carbonated water, if preferred.)', 'https://www.thecocktaildb.com/images/media/drink/dhvr7d1504519752.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11339, 'English Rose Cocktail', 'Ordinary Drink', 'Cocktail glass', 'Rub rim of cocktail glass with lemon juice and dip rim of glass in powdered sugar. Shake all ingredients (except cherry) with ice and strain into sugar-rimmed glass. Top with the cherry and serve.', 'https://www.thecocktaildb.com/images/media/drink/yxwrpp1441208697.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11368, 'Flying Dutchman', 'Ordinary Drink', 'Old-fashioned glass', 'In an old-fashioned glass almost filled with ice cubes, combine the gin and triple sec. Stir well.', 'https://www.thecocktaildb.com/images/media/drink/mwko4q1582482903.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11369, 'Flying Scotchman', 'Ordinary Drink', 'Cocktail glass', 'Shake all ingredients with ice, strain into a cocktail glass, and serve.', 'https://www.thecocktaildb.com/images/media/drink/q53l911582482518.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11387, 'Frozen Daiquiri', 'Ordinary Drink', 'Champagne flute', 'Combine all ingredients (except for the cherry) in an electric blender and blend at a low speed for five seconds, then blend at a high speed until firm. Pour contents into a champagne flute, top with the cherry, and serve.', 'https://www.thecocktaildb.com/images/media/drink/7oyrj91504884412.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11390, 'Frozen Mint Daiquiri', 'Ordinary Drink', 'Old-fashioned glass', 'Combine all ingredients with 1 cup of crushed ice in an electric blender. Blend at a low speed for a short length of time. Pour into an old-fashioned glass and serve.', 'https://www.thecocktaildb.com/images/media/drink/jrhn1q1504884469.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11391, 'Frozen Pineapple Daiquiri', 'Ordinary Drink', 'Cocktail Glass', 'Combine all ingredients with 1 cup of crushed ice in an electric blender. Blend at a low speed for a short length of time. Pour into a cocktail glass and serve.', 'https://www.thecocktaildb.com/images/media/drink/k3aecd1582481679.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11396, 'Gentleman''s Club', 'Ordinary Drink', 'Old-fashioned glass', 'In an old-fashioned glass almost filled with ice cubes, combine all of the ingredients. Stir well.', 'https://www.thecocktaildb.com/images/media/drink/k2r7wv1582481454.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11403, 'Gin And Tonic', 'Ordinary Drink', 'Highball glass', 'Pour the gin and the tonic water into a highball glass almost filled with ice cubes. Stir well. Garnish with the lime wedge.', 'https://www.thecocktaildb.com/images/media/drink/k0508k1668422436.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11407, 'Gin Cooler', 'Ordinary Drink', 'Collins glass', 'Stir powdered sugar and 2 oz. carbonated water in a collins glass. Fill glass with ice and add gin. Fill with carbonated water and stir. Add the lemon peel and the orange spiral so that the end of the orange spiral dangles over rim of glass.', 'https://www.thecocktaildb.com/images/media/drink/678xt11582481163.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11408, 'Gin Daisy', 'Ordinary Drink', 'Old-fashioned glass', 'In a shaker half-filled with ice cubes, combine the gin, lemon juice, sugar, and grenadine. Shake well. Pour into an old-fashioned glass and garnish with the cherry and the orange slice.', 'https://www.thecocktaildb.com/images/media/drink/z6e22f1582581155.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11410, 'Gin Fizz', 'Ordinary Drink', 'Highball glass', 'Shake all ingredients with ice cubes, except soda water. Pour into glass. Top with soda water.', 'https://www.thecocktaildb.com/images/media/drink/drtihp1606768397.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11415, 'Gin Sling', 'Ordinary Drink', 'Old-fashioned glass', 'Dissolve powdered sugar in mixture of water and juice of lemon. Add gin. Pour into an old-fashioned glass over ice cubes and stir. Add the twist of orange peel and serve.', 'https://www.thecocktaildb.com/images/media/drink/8cl9sm1582581761.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11416, 'Gin Smash', 'Ordinary Drink', 'Old-fashioned glass', 'Muddle sugar with carbonated water and mint sprigs in an old-fashioned glass. Add gin and 1 ice cube. Stir, add the orange slice and the cherry, and serve.', 'https://www.thecocktaildb.com/images/media/drink/iprva61606768774.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11417, 'Gin Sour', 'Ordinary Drink', 'Whiskey sour glass', 'In a shaker half-filled with ice cubes, combine the gin, lemon juice, and sugar. Shake well. Strain into a sour glass and garnish with the orange slice and the cherry.', 'https://www.thecocktaildb.com/images/media/drink/noxp7e1606769224.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11418, 'Gin Squirt', 'Ordinary Drink', 'Highball glass', 'Stir gin, grenadine, and powdered sugar with ice and strain into a highball glass over ice cubes. Fill with carbonated water and stir. Decorate with the pineapple chunks and the strawberries and serve.', 'https://www.thecocktaildb.com/images/media/drink/xrbhz61504883702.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11419, 'Gin Swizzle', 'Ordinary Drink', 'Highball glass', 'In a shaker half-filled with ice cubes, combine the lime juice, sugar, gin, and bitters. Shake well. Almost fill a colling glass with ice cubes. Stir until the glass is frosted. Strain the mixture in the shaker into the glass and add the club soda.', 'https://www.thecocktaildb.com/images/media/drink/sybce31504884026.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11420, 'Gin Toddy', 'Ordinary Drink', 'Old-fashioned glass', 'Mix powdered sugar and water in an old-fashioned glass. Add gin and one ice cube. Stir, add the twist of lemon peel, and serve.', 'https://www.thecocktaildb.com/images/media/drink/jxstwf1582582101.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11422, 'Godchild', 'Ordinary Drink', 'Champagne flute', 'Shake all ingredients well with cracked ice, strain into a champagne flute, and serve.', 'https://www.thecocktaildb.com/images/media/drink/m5nhtr1504820829.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11424, 'Godmother', 'Ordinary Drink', 'Old-fashioned glass', 'Pour vodka and amaretto into an old-fashioned glass over ice and serve.', 'https://www.thecocktaildb.com/images/media/drink/quksqg1582582597.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11433, 'Grass Skirt', 'Ordinary Drink', 'Old-fashioned glass', 'In a shaker half-filled with ice cubes, combine the gin, triple sec, pineapple juice, and grenadine. Shake well. Pour into an old-fashioned glass and garnish with the pineapple slice.', 'https://www.thecocktaildb.com/images/media/drink/qyvprp1473891585.jpg');
INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11462, 'Harvey Wallbanger', 'Ordinary Drink', 'Collins glass', 'Stir the vodka and orange juice with ice in the glass, then float the Galliano on top. Garnish and serve.', 'https://www.thecocktaildb.com/images/media/drink/7os4gs1606854357.jpg');
