INSERT INTO cocktails (id, name, category, glass, instruction, thumb) VALUES (11580, 'John Collins', 'Ordinary Drink', 'Collins glass', 'Pour all ingredients directly into highball glass filled with ice. Stir gently. Garnish. Add a dash of Angostura bitters.', 'https://www.thecocktaildb.com/images/media/drink/0t4bv71606854479.jpg');
